import React, { useState, useEffect } from 'react';
import './styles/login.css';

function Login(){

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault(); // Prevent the default form submit action
        setError(''); // Clear any previous errors

        // Here, you would replace 'http://yourapi.com/login' with your actual API endpoint
        const endpoint = 'http://yourapi.com/login';
        const credentials = { login: email, password: password };

        try {
          const response = await fetch(endpoint, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(credentials),
          });

          const data = await response.json();

          if (response.ok) {
            // Handle success - maybe set user state or redirect
            alert('Login Successful!');
          } else {
            // Handle errors
            setError(data.message || 'An error occurred during login.');
          }
        } catch (error) {
          // Handle network errors
          setError('Network error: ' + error.message);
        }
      };

    return (
        <div id='div__container'>
            <h1 id='titulo__form'>Inicio de sesión</h1>

            <form id='form__container' onSubmit={handleLogin}>
                <div className="form__group" id='mail__input'>
                    <label>Correo electrónico</label>
                    <input type="text" className="form__control" aria-describedby="emailHelp" value={email} onChange={e => setEmail(e.target.value)}/>
                </div>
                <div className="form__group" id='password__input'>
                    <label>Contraseña</label>
                    <input type="password" className="form__control" value={password} onChange={e => setPassword(e.target.value)}/>
                </div>

                <div id='btn__container'>
                    <button className='boton' type="submit" id='login__btn'>Ingresar</button>
                    <button className='boton' type="text" id='cancel__btn'>Cancelar</button>
                </div>
            </form>
        </div>
    )
}

export default Login;
