import React, { useState } from 'react';
import './styles/login.css';

function Login(){

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();

        const response = await fetch('http://localhost:3001/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ login: email, password: password }),
        });

        const data = await response.json();

        // Actualiza los estados email y password con los valores devueltos por el fetch
        setEmail(data.login);
        setPassword(data.password);

        // Compara los valores de los inputs con los valores devueltos por el fetch
        if (email === data.login && password === data.password) {
            alert('Registro exitoso');
        } else {
            alert('Registro no válido');
        }
    };

    return (
        <div id='div__container'>
            <h1 id='titulo__form'>Inicio de sesión</h1>

            <form id='form__container' onSubmit={handleSubmit}>
                <div className="form__group" id='mail__input'>
                    <label>Correo electrónico</label>
                    <input type="text" className="form__control" aria-describedby="emailHelp" value={email} onChange={e => setEmail(e.target.value)}/>
                </div>
                <div className="form__group" id='password__input'>
                    <label>Contraseña</label>
                    <input type="password" className="form__control" value={password} onChange={e => setPassword(e.target.value)}/>
                </div>

                <div id='btn__container'>
                    <button className='boton' type="submit" id='login__btn'>Ingresar</button>
                    <button className='boton' type="text" id='cancel__btn'>Cancelar</button>
                </div>
            </form>
        </div>
    )
}

export default Login;
