import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles/login.css';
import Carros from './carros';
import { FormattedMessage } from 'react-intl';

function Login(){

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const credentials = {login: 'admin', password: 'pass'};
    const [isLogged, setIsLogged] = useState(false);


    /*
    const handleLogin = () =>{
        if (email === credentials.login && password === credentials.password){
            setIsLogged(true);
            console.log('Login successful');
        }
        else{

            setError('Error de autenticaci贸n. Revise sus credenciales');
            console.log(error);
        }
    };
    */

    const handleCancel = () =>{
        setEmail('');
        setPassword('');
        setError('');
    }

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
          const response = await axios.post('http://localhost:3001/login', {
            login: email,
            password: password
          });

          setLoggedIn(true);
          onLoginSuccess(); 
        } catch (error) {
          if (error.response && error.response.data && error.response.data.message) {
            setErrorMessage(error.response.data.message);
          } else {
            setErrorMessage('Ocurri贸 un error al procesar la solicitud');
          }
        }
      };


    /*
    const handleLogin = async (e) => {
        e.preventDefault(); // Prevent the default form submit action
        setError(''); // Clear any previous errors

        const endpoint = ' http://localhost:3001/login';
        const credentials = {login: email, password: password};

        try {
          const response = await axios.post(endpoint, credentials);

          if (response.status === 200) {
            alert('Login Successful!');
          } else {
            setError(response.data.message || 'An error occurred during login.');
          }
        } catch (error) {
          setError('Network error: ' + error.message);
        }
    };
    */

    return isLogged ? <Carros/> : (
        <div id='div__container'>
            <h1 id='titulo__form'>

                <FormattedMessage id="Log-in page"/>
            </h1>

            <form id='form__container'>
                <div className="form__group" id='mail__input'>
                    <label>
                        <FormattedMessage id="Username"/>
                    </label>
                    <input type="text" className="form__control" aria-describedby="emailHelp" value={email} onChange={e => setEmail(e.target.value)}/>
                </div>
                <div className="form__group" id='password__input'>
                    <label>
                        <FormattedMessage id="Password"/>
                    </label>
                    <input type="text" className="form__control" value={password} onChange={e => setPassword(e.target.value)}/>
                </div>

                <div id='btn__container'>
                    <button className='boton' type="button" id='login__btn' onClick={handleLogin}>
                        <FormattedMessage id="Log in"/>
                    </button>
                    <button className='boton' type="button" id='cancel__btn' onClick={handleCancel}>
                        <FormattedMessage id="Cancel"/>
                    </button>
                </div>
            </form>
            <p id='error__message'>{error}</p>
        </div>
    );
}

export default Login;
